import React, { useState } from 'react';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function Register() {

    const [publishDate, setDate] = useState(new Date());
    const [description, setDescription] = useState('');
    const [file, setFile] = useState('');
    const [category, setCategory] = useState('');
    const [report, setReport] = useState('');

    let token = localStorage.getItem('token');

    const clearForm = (e) => {
        e.preventDefault();
        reset();
    }

    const handleCategory = (categoryKey) => {
        setCategory(categoryKey);
    }

    const handleReport = (report) => {
        console.log(`Selected Report : ${report} `);
        setReport(report)
    }

    const fileHandler = event => {
        setFile(event.target.files[0])
    }

    const reset = () => {
        window.location.reload(true);
    }

    const submit = (event) => {
        event.preventDefault();
        if (!description) {
            alert(`Description is required`);
        }
        else {
            const formData = new FormData();
            formData.append('category', category);
            formData.append('report', report);
            formData.append('publishDate', publishDate);
            formData.append('description', description);
            formData.append('files', file);

            fetch('http://localhost:8100/api/archiveReport/upload', {
                method: 'POST',
                headers: {
                    'X-ACCESS-TOKEN': token
                },
                body: formData
            })
                .then((response) => {
                    return response.json()
                })
                .then((responseJson) => {
                    console.log(JSON.stringify(responseJson))
                    if (responseJson.success) {
                        alert("Report Added successfully");
                    }
                    else {
                        alert("Error occured");
                    }
                    reset();

                });
        }
    }

    return (
        <Container>
            <h1>Registration Form</h1>
            <br></br>
            <Form>
                <br></br>
                <Form.Group controlId="FormPublishDate">
                    <Form.Label>Enter Publish Date</Form.Label>
                    <DatePicker selected={publishDate} onChange={publishDate => setDate(publishDate)} />
                </Form.Group>
                <Form.Group controlId="FormDescription">
                    <Form.Label>Enter Description</Form.Label>
                    <Form.Control
                        value={description}
                        type='description'
                        placeholder='Enter Description'
                        style={{ height: '100px' }}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                </Form.Group>
                <Form.Group controlId="FormFile">
                    <Form.Label>Enter File</Form.Label>
                    <input type="file" name="file" onChange={fileHandler} />
                </Form.Group>
            </Form>
            <Row>
                <Col>
                    <Button variant='primary' type='submit' onClick={(event) => submit(event)}>Submit</Button>
                </Col>
                <Col>
                    <Button variant='primary' onClick={(event) => clearForm(event)}>clear</Button>
                </Col>
            </Row>
        </Container>
    )
}

export default Register;
